ytd-app {
    overflow: auto !important;
}
ytd-app[scrolling] {
    position: absolute !important;
    top: 0 !important;
    left: 0 !important;
    right: calc((var(--ytd-app-fullerscreen-scrollbar-width) + 1px)*-1) !important;
    bottom: 0 !important;
    overflow-x: auto !important;
}
ytd-watch-flexy[full-bleed-player] #single-column-container.ytd-watch-flexy,
ytd-watch-flexy[full-bleed-player] #columns.ytd-watch-flexy {
    display: flex !important;
}
.ytp-fullscreen-grid-peeking.ytp-full-bleed-player.ytp-delhi-modern:not(.ytp-autohide) .ytp-chrome-bottom {
    bottom: 0 !important;
    opacity: 1!important;
}
#movie_player:not(.ytp-grid-ended-state) .ytp-fullscreen-grid {
    display: none!important;
    top: 100%!important;
    opacity: 0!important;
}

/* =========================
   1. 删除侧边栏（TOC）
   ========================= */
/* 你定位到的目录侧边栏 */
.ltx_TOC.active,
.ltx_TOC {
    display: none !important;
}


/* =========================
   2. 删除顶栏（Header）
   ========================= */
.desktop_header {
    display: none !important;
}
.mob_header {
    display: none !important;
}


/* =========================
   3. 删除悬浮“Report Issue”按钮
   ========================= */
#openForm,
button#openForm,
.btn.btn-primary.hover-rp-button {
    display: none !important;
}



/* =========================
   4. 删除渲染错误提示块
   ========================= */
/* class="ltx_ERROR undefined" */
.ltx_ERROR.undefined {
    display: none !important;
}


/* =========================
   5. 删除左上角 Beta 标志
   ========================= */
body::after {
    content: none !important;
    display: none !important;
}

/* =========================
   6. 删除 LaTeX package 警告块
   ========================= */
/* class="package-alerts ltx_document" */
.package-alerts.ltx_document {
    display: none !important;
}

/* =========================
   7. arXiv 图片白底垫板（暗黑模式友好）
   ========================= */
img,
svg {
    background-color: white !important;
}

/* =========================
   8. 修复 arXiv 表格线框（暗黑模式可读）
   ========================= */
.ltx_page_content table,
.ltx_page_content th,
.ltx_page_content td {
    border-color: #ccc !important;
    /* 浅灰色，和深背景对比 */
}

/* =========================
   9. 修复 arXiv 宽表格左列被遮挡
   ========================= */
.ltx_flex_figure,
.ltx_flex_cell,
.ltx_transformed_outer,
.ltx_transformed_inner {
    transform: none !important;
    width: 100% !important;
}
